import Image from './popupImage';
const element = document.querySelector('.button_type_edit');
function App() {


  let isClicked = false;

  element.addEventListener('click', () => {
      isClicked = true;
  }); 
function handleEditAvatarClick() {
    document.querySelector('.popup_avatar').classList.add('popup_is-opened')
}
function handleEditAddClick() {
    document.querySelector('.popup_add').classList.add('popup_is-opened')
}
  return (
  <>
