    <Form   title = {'Редактировать профиль'} name = {'edit'}/>
    <Form   title = {'Удалить карточку'} name = {'delete'}/>
    <Main onEditProfile ={isClicked ? 'popup_is-opened' : ''} onAddPlace={handleEditAddClick} onEditAvatar={handleEditAvatarClick}/>
    <Image/>
    <Footer />
    </div>
