import Vector2 from '../images/Vector2.svg'
import Close from '../images/Close-icon.svg'
import Main from './main'
import Footer from './footer';
import Header from './header';
import PopupWithForm from './popupWithForm';
import ImagePopup from './popupImage';
import React from 'react'
import api from '../utils/api';
// import Card from './card'

function App() {
const [userName, setName] = React.useState([])
const [cards, setCards] = React.useState([])
const [userDescription, setDescription] = React.useState([])
const [userAvatar, setAvatar] = React.useState([])
const [card, setCard] = React.useState(null)
React.useEffect(()=>{
  api.getInitialCards()
  .then((res)=>{
    setCards(res.map((card)=>({
      id: card._id,
      likes: card.likes,
      url: card.link,
      name: card.name
    })))
  })
}, [])


    React.useEffect(()=>{
        api.getUserId()
        .then((res)=>{
            setName(res.name)
            setDescription(res.about)
            setAvatar(res.avatar)
        })
    })


  const [isEditProfilePopupOpen, editOpen] = React.useState(false) ;
  const [isEditAvatarPopupOpen, avatarOpen] = React.useState(false) ;
  const [isAddPlacePopupOpen, placeOpen] = React.useState(false) ;
  const [cardSelected, selectedCard] = React.useState(null) ;

function handleCardClick(){
  selectedCard(true)
}

function handleEditEditClick () {

  editOpen(true)
}
function handleEditAvatarClick(){
  avatarOpen(true)
}
function handleEditAddClick () {

placeOpen(true)
}
function closeAllPopups(){
  placeOpen(false)
  avatarOpen(false)
  editOpen(false)
  selectedCard(false)
}
  return (
  <>
    <div className="page">
    <Header />
    <PopupWithForm   title = {'Добавить карточку'} name = {'add'} isOpen={isAddPlacePopupOpen ? 'popup_is-opened' : ''} onClose={closeAllPopups} button={'Добавить'} />
    <PopupWithForm   title = {'Обновить профиль'} name = {'avatar'} isOpen={isEditAvatarPopupOpen ? 'popup_is-opened' : ''} onClose={closeAllPopups} button={"Сохранить"}/>
    <PopupWithForm  title = {'Редактировать профиль'} name = {'edit'} isOpen={isEditProfilePopupOpen ? 'popup_is-opened' : ''} onClose={closeAllPopups} button={"Сохранить"}/>
    <PopupWithForm   title = {'Удалить карточку'} name = {'delete'} button={"Да"}/>
    <Main card={cardSelected} userName={userName} onEditProfile ={handleEditEditClick} onAddPlace={handleEditAddClick} onEditAvatar={handleEditAvatarClick} userAvatar={userAvatar} userDescription={userDescription} cards={cards} onCardClick={handleCardClick} onCardClick={handleCardClick}/>
    <ImagePopup card={card} onClose={closeAllPopups}/>

    <Footer />
    </div>

</>
  );
}

export default App;
