import Vector2 from '../images/Vector2.svg'
import Close from '../images/Close-icon.svg'
import Main from './main'
import Footer from './footer';
import Header from './header';
import Form from './popupWithForm';
import Image from './popupImage';
import React from 'react'
import api from '../utils/api';

function App() {
const [userNames, setName] = React.useState([])
const [userDescription, setDescription] = React.useState([])
const [userAvatar, setAvatar] = React.useState([])
    React.useEffect(()=>{
        api.getUserId()
        .then((res)=>{
            setName(res.name)
            setDescription(res.about)
            setAvatar(res.avatar)
        })
    })

  const [isEditProfilePopupOpen, editOpen] = React.useState(false) ;
  const [isEditAvatarPopupOpen, avatarOpen] = React.useState(false) ;
  const [isAddPlacePopupOpen, placeOpen] = React.useState(false) ;

function handleEditEditClick () {

  editOpen(true)
}
function handleEditAvatarClick(){
  avatarOpen(true)
}
function handleEditAddClick () {

placeOpen(true)
}
function closeAllPopups(){
  placeOpen(false)
  avatarOpen(false)
  editOpen(false)
}
  return (
  <>
   <div className="page">
    <Header />
    <Form   title = {'Добавить карточку'} name = {'add'} isOpen={isAddPlacePopupOpen ? 'popup_is-opened' : ''} onClose={closeAllPopups} button={'Добавить'} />
    <Form   title = {'Обновить профиль'} name = {'avatar'} isOpen={isEditAvatarPopupOpen ? 'popup_is-opened' : ''} onClose={closeAllPopups} button={"Сохранить"}/>
    <Form   title = {'Редактировать профиль'} name = {'edit'} isOpen={isEditProfilePopupOpen ? 'popup_is-opened' : ''} onClose={closeAllPopups} button={"Сохранить"}/>
    <Form   title = {'Удалить карточку'} name = {'delete'} button={"Да"}/>
    <Main userName={userNames} onEditProfile ={handleEditEditClick} onAddPlace={handleEditAddClick} onEditAvatar={handleEditAvatarClick} userAvatar={userAvatar} userDescription={userDescription}/>
    <Image/>
    <Footer />
    </div>
    {/* <template id="template">
        <div className="element">
            <button className="button button_type_delete" type="button"><img className="element__delete" src={Close} alt="Удаление"/></button>
            <button className="button button_type_image" type="button"><img className="element__image" src="" alt="Картинка"/></button>
            <div className="element__group">
                <h2 className="element__title"></h2>
                <div className="element__like_groupe">
                <button className="button button_type_like" type="button"><img className="element__like" src={Vector2} alt="Лайк"/></button>
                <p className="element__like_number"></p>
                </div>
            </div>
        </div>
    </template> */}
</>
  );

}

export default App;
